import React, { Component } from 'react';
import "./App.css"
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {value: 'Male',loading: true, drizzleState: null,stackId:null};
    this.handleChange = this.handleChange.bind(this);
    this.formGenerator = this.formGenerator.bind(this);
    this.genderGenerator=this.genderGenerator.bind(this);
    this.form=this.form.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    const { drizzle } = this.props;
    // subscribe to changes in the store
    this.unsubscribe = drizzle.store.subscribe(() => {
      // every time the store updates, grab the state from drizzle
      const drizzleState = drizzle.store.getState();
      // check to see if it's ready, if so, update local component state
      if (drizzleState.drizzleStatus.initialized) {
        this.setState({ loading: false, drizzleState });
      }
    });
  }

  compomentWillUnmount() {
    this.unsubscribe();
  }

  setValue = (first,last,father,age,gender,addr) => {
    const { drizzle, drizzleState } = this.props;
    const contract = drizzle.contracts.Citizenship;
// let drizzle know we want to call the `add` method with `value1 and value2`
    const stackId = contract.methods["addCitizen"].cacheSend(this.textInput1.value, this.textInput2.value, {
      from: drizzleState.accounts[0]
    });
// save the `stackId` for later reference
    this.setState({ stackId });
  };

  render() {
    if (this.state.loading) return "Loading Citizenship...";
    return (
      <div className="App">
        {this.form()}

      </div>
    );
  }
  handleSubmit(){
    alert("yo")
  }

  form(){
      return(
      <div>
      <h1 className="header">Register a Citizen</h1>
      <form className="form-horizontal" onSubmit={this.handleSubmit}>
        <fieldset>
        {this.formGenerator("firstName","firstName","First Name","text")}
        {this.formGenerator("lastName","lastName","Last Name","text")}
        {this.formGenerator("father","father","Fathers Name","text")}
        {this.formGenerator("Age","Age","Age","number")}
        {this.genderGenerator()}
        {this.formGenerator("Address","Address","Address","text")}
        </fieldset>
        <input type="submit" value="Submit" />
      </form>     

      </div>);
  }

  formGenerator(_id,name,placeholder,type){
    return(<div className="form-group">
    <div class= "col-md-16 inputGroupContainer">
      <div class="input-group"><span class="input-group-addon"></span><input id={_id} name={name} placeholder={placeholder} class="form-control" required="true"  type={type} ></input>
      </div>
    </div>
  </div>)
  }

  genderGenerator(){
    return(
      <div className ="Gender">
      <label> Gender:
      <select value={this.state.value} onChange={this.handleChange}>
        <option value="Male">Male</option>
        <option value="Female">Female</option>
        <option value="Other">Other</option>
      </select>
      </label>
    </div>
    )
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

}

export default App;
