import React, { Component } from 'react';
import "./App.css"
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {value: 'Male',loading: true, drizzleState: null};
    this.handleChange = this.handleChange.bind(this);
    this.formGenerator = this.formGenerator.bind(this);
    this.genderGenerator=this.genderGenerator.bind(this);
  }
  formGenerator(_id,name,placeholder,type){
    return(<div className="form-group">
    <div class= "col-md-16 inputGroupContainer">
      <div class="input-group"><span class="input-group-addon"></span><input id={_id} name={name} placeholder={placeholder} class="form-control" required="true" value="" type={type}></input>
      </div>
    </div>
  </div>)
  }
  genderGenerator(){
    return(
      <div className ="Gender">
      <label> Gender:
      <select value={this.state.value} onChange={this.handleChange}>
        <option value="Male">Male</option>
        <option value="Female">Female</option>
        <option value="Other">Other</option>
      </select>
      </label>
    </div>
    )
  }
  handleChange(event) {
    this.setState({value: event.target.value});
  }
  componentDidMount() {
    const { drizzle } = this.props;
    // subscribe to changes in the store
    this.unsubscribe = drizzle.store.subscribe(() => {
      // every time the store updates, grab the state from drizzle
      const drizzleState = drizzle.store.getState();
      // check to see if it's ready, if so, update local component state
      if (drizzleState.drizzleStatus.initialized) {
        this.setState({ loading: false, drizzleState });
      }
    });
  }
  render() {
    return (
      <div className="App">
          <h1 className="header">Register a Citizen</h1>
          <form className="form-horizontal">
            <fieldset>
            {this.formGenerator("firstName","firstName","First Name","text")}
            {this.formGenerator("lastName","lastName","Last Name","text")}
            {this.formGenerator("father","father","Fathers Name","text")}
            {this.formGenerator("Age","Age","Age","number")}
            {this.genderGenerator()}
            {this.formGenerator("Address","father","Fathers Name","text")}
            <div className="form-group">
              <div class= "col-md-16 inputGroupContainer">
                <div class="input-group"><span class="input-group-addon"></span><input id="Address" name="Address" placeholder="Address" class="form-control" required="true" value="" type="text"></input>
                </div>
              </div>
            </div>
            </fieldset>
          </form>     

          <button type="button" class="btn btn-primary btn-block">Submit</button>
      </div>
    );
  }

}

export default App;
